import React from "react"


export default function sidebar(props)
{
    const noteElements = props.notes.map((note,index) => (
       <div key={note.id}>
           <h4 className="text-snippet">Note {index + 1}</h4>
       </div>
    ))
    return (
        {noteElements}

    )
}
