import React from "react"
import Sidebar from "./components/Sidebar"
import Editor from "./components/Editor"
import { data } from "./data"
import Split from "react-split"
import {nanoid} from "nanoid"

export default function App() {


  /*
     * Lazily initialize our `notes` state so it doesn't
     * reach into localStorage on every single re-render
     * of the App component
   */

    const [notes, setNotes] = React.useState( () => JSON.parse(localStorage.getItem("notes")) || [])

    console.log("firstnotes",notes)


    const [currentNoteId, setCurrentNoteId] = React.useState(
        (notes[0] && notes[0].id) || ""
    )


    console.log("currentNoteId",currentNoteId)

    React.useEffect(() => {
        localStorage.setItem("notes",JSON.stringify(notes))
      //  console.log(notes[0].body.split("\n"))


    },[notes])

    function createNewNote() {
        const newNote = {
            id: nanoid(3),
            body: "# Type your markdown title"
        }
        setNotes(prevNotes => [newNote, ...prevNotes])

        setCurrentNoteId(newNote.id)
    }

    function updateNote(text) {
        // Try to rearrange the most recently-modified
        // not to be at the top
        setNotes(oldNotes => {
            // Create a new empty array
            // Loop over the original array
            // if the id matches
            // put the updated note at the
            // beginning of the new array
            // else
            // push the old note to the end
            // of the new array
            // return the new array
        })

        // This does not rearrange the notes
        // setNotes(oldNotes => oldNotes.map(oldNote => {
        //     return oldNote.id === currentNoteId
        //         ? { ...oldNote, body: text }
        //         : oldNote
        // }))
    }

    function findCurrentNote() {
        return notes.find(note => {
            return note.id === currentNoteId
        }) || notes[0]
    }

    return (
        <main>
            {
                notes.length > 0
                    ?
                    <Split
                        sizes={[30, 70]}
                        direction="horizontal"
                        className="split"
                    >
                        <Sidebar
                            notes={notes}
                            currentNote={findCurrentNote()}
                            setCurrentNoteId={setCurrentNoteId}
                            newNote={createNewNote}
                        />
                        {
                            currentNoteId &&
                            notes.length > 0 &&
                            <Editor
                                currentNote={findCurrentNote()}
                                updateNote={updateNote}
                            />
                        }
                    </Split>
                    :
                    <div className="no-notes">
                        <h1>You have no notes</h1>
                        <button
                            className="first-note"
                            onClick={createNewNote}
                        >
                            Create one now
                        </button>
                    </div>

            }
        </main>
    )
}
